import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

import MessageCard from '../components/MessageCard';

import socket from '../utils/socket';

const messages = [
  { name: 'teste1', message: 'dasdsa' },
  { name: 'teste2', message: 'fgadfsa' },
  { name: 'teste3', message: 'adsdsadsa' }
]

const handleMessage = ({ user, name, message }) => socket.emit('sendMessage', { user, name, message });

const Main = () => {
  const { user } = useParams();
  const [name, setName] = useState('Anônimo')
  const [message, setMessage] = useState('');

  const depoiment = { user, name, message };

  return (
    <main>
      <h1>Perfil de { user }</h1>

      <div>
        { messages.map(({ name, message }) => <MessageCard name={ name } message={ message } />) }
      </div>

      <div>
        <input
          placeholder="Nome"
          onChange={ ({ target: { value } }) => setName(value) }
        />
        <textarea
          type="text"
          onChange={ ({ target: { value } }) => setMessage(value) }
          />
        <button
          type="button"
          onClick={() => handleMessage(depoiment)}
        >
          Enviar
        </button>
      </div>
    </main>
  );
};

export default Main;
