import React, { useState } from 'react';

const Login = () => {
  const [name, setName] = useState('Anônimo');

  return (
    <form>
      <input
        type="text"
        placeholder='Como você gostaria de ser chamado?'
        onChange={ setName }
      />
    <button
      type="submit"
      onClick={ console.log(name) }
    >
      Entrar
    </button>
    </form>
  )
}

export default Login;
