import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

import MessageCard from '../components/MessageCard';

import socket from '../utils/socket';

const handleMessage = ({ user, name, message }) => console.log(message);

const Main = () => {
  const { user } = useParams();
  const [name, setName] = useState('Anônimo')
  const [message, setMessage] = useState('');

  const [messages, setMessages] = useState([]);

  const [loading, setLoading] = useState(true);

  const depoiment = { user, name, message };

  useEffect(() => {
    fetch(`http://localhost:3001/depoimentos/${ user }`)
      .then(res => res.json())
      .then((depoiments) => {
        setLoading(false);
        setMessages(depoiments);
      })
  })

  if (loading) return <p>Carregando...</p>

  return (
    <main>
      <h1>Perfil de { user }</h1>

      <div>
        { messages.map(({ name, message }) => <MessageCard name={ name } message={ message } />) }
      </div>

      <div>
        <input
          placeholder="Nome"
          onChange={ ({ target: { value } }) => setName(value) }
        />
        <textarea
          type="text"
          onChange={ ({ target: { value } }) => setMessage(value) }
          />
        <button
          type="button"
          onClick={() => handleMessage(depoiment)}
        >
          Enviar
        </button>
      </div>
    </main>
  );
};

export default Main;
