import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

import MessageCard from '../components/MessageCard';

const messages = [
  { name: 'teste1', message: 'dasdsa' },
  { name: 'teste2', message: 'fgadfsa' },
  { name: 'teste3', message: 'adsdsadsa' }
]

const Main = () => {
  const { user } = useParams();
  const [name, setName] = useState('Anônimo')
  const [message, setMessage] = useState('');

  return (
    <main>
      <h1>Perfil de { user }</h1>

      <div>
        { messages.map(({ name, message }) => <MessageCard name={ name } message={ messag } />) }
      </div>

      <div>
        <input
          placeholder="Nome"
          onChange={ ({ target: { value } }) => setName(value) }
        />
        <textarea
          type="text"
          onChange={ ({ target: { value } }) => setMessage(value) }
          />
        <button
          type="button"
          onClick={ () => console.log({name, message}) }
        >
          Enviar
        </button>
      </div>
    </main>
  );
};

export default Main;
